export function generateRandomKey(t){return CryptoJS.lib.WordArray.random(t).toString(CryptoJS.enc.Base64)}export function generateRandomHex(t){return CryptoJS.lib.WordArray.random(t).toString(CryptoJS.enc.Hex)}export function generateRandomPass(t){const r="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!\"#$%&'*+,./:;=?@^`|~_";let o="";for(let n=0;n<t;n++){o+=r[Math.floor(84*Math.random())]}return o}export function encryptText(t,r){return r=CryptoJS.SHA256(r).toString(),CryptoJS.AES.encrypt(t,r).toString()}export function decryptText(t,r){return r=CryptoJS.SHA256(r).toString(),CryptoJS.AES.decrypt(t,r).toString(CryptoJS.enc.Utf8)}